package com.meirusfandi.cataloguemovie.data.local;import android.database.Cursor;import android.net.Uri;import android.provider.BaseColumns;import com.meirusfandi.cataloguemovie.R;public class DatabaseContract {    public static String TABLE_FAVORITE    = "favorite";    public static final class FavoriteColumns implements BaseColumns {        public static String TITLE         = "title";        public static String RELEASE       = "release";        public static String OVERVIEW      = "overview";        public static String POSTER        = "poster";        public static String BACKDROP      = "backdrop";        public static String RATE_AVG      = "rate_avg";        public static String POPULARITY    = "popularity";    }    public static final String AUTHORITY = "com.meirusfandi.cataloguemovie";    public static final Uri CONTENT_URI = new Uri.Builder().scheme("content")            .authority(AUTHORITY)            .appendPath(TABLE_FAVORITE)            .build();    public static String getColumnString(Cursor cursor, String columnName) {        return cursor.getString( cursor.getColumnIndex(columnName) );    }    public static int getColumnInt(Cursor cursor, String columnName) {        return cursor.getInt( cursor.getColumnIndex(columnName) );    }    public static long getColumnLong(Cursor cursor, String columnName) {        return cursor.getLong( cursor.getColumnIndex(columnName) );    }}