package com.meirusfandi.cataloguemovie.data.local;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;import static android.provider.BaseColumns._ID;import static com.meirusfandi.cataloguemovie.data.local.DatabaseContract.TABLE_FAVORITE;public class DatabaseHelper extends SQLiteOpenHelper{    private static String DB_NAME        = "movie";    private static final int DB_VERSION  = 1;    private static final String CREATE_TABLE = String.format("CREATE TABLE %s" +                    "(%s INTEGER NOT NULL PRIMARY KEY, " +                    "%s TEXT NOT NULL," +                    "%s TEXT NOT NULL," +                    "%s TEXT NOT NULL," +                    "%s TEXT NOT NULL," +                    "%s TEXT NOT NULL," +                    "%s TEXT NOT NULL," +                    "%s TEXT NOT NULL)",            TABLE_FAVORITE,            _ID,            DatabaseContract.FavoriteColumns.TITLE,            DatabaseContract.FavoriteColumns.RELEASE,            DatabaseContract.FavoriteColumns.OVERVIEW,            DatabaseContract.FavoriteColumns.POSTER,            DatabaseContract.FavoriteColumns.BACKDROP,            DatabaseContract.FavoriteColumns.RATE_AVG,            DatabaseContract.FavoriteColumns.POPULARITY);    DatabaseHelper(Context context) {        super(context, DB_NAME, null, DB_VERSION);    }    @Override    public void onCreate(SQLiteDatabase db) {        db.execSQL(CREATE_TABLE);    }    @Override    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {        db.execSQL("DROP TABLE IF EXISTS "+TABLE_FAVORITE);        onCreate(db);    }}