package com.meirusfandi.cataloguemovie.data.network;import com.meirusfandi.cataloguemovie.BuildConfig;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Query;public interface MovieInterface {    @GET(BuildConfig.SEARCH_MOVIE)    Call<MovieResponse> getSearchMovie(@Query("api_key") String API_KEY, @Query("language") String language , @Query("query") String judul);    @GET(BuildConfig.NOWPLAYING_MOVIE)    Call<MovieResponse> getNowPlaying(@Query("api_key") String API_KEY, @Query("language") String language);    @GET(BuildConfig.UPCOMING_MOVIE)    Call<MovieResponse> getUpcoming(@Query("api_key") String API_KEY, @Query("language") String language);}