package com.meirusfandi.cataloguemovie.views.main;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.design.widget.BottomNavigationView;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentTransaction;import android.support.v4.view.GravityCompat;import android.support.v4.widget.DrawerLayout;import android.support.v7.app.ActionBar;import android.support.v7.app.AppCompatActivity;import android.view.MenuItem;import android.widget.TextView;import com.meirusfandi.cataloguemovie.R;import com.meirusfandi.cataloguemovie.views.favorite.FavoriteFragment;import com.meirusfandi.cataloguemovie.views.home.HomeFragment;import com.meirusfandi.cataloguemovie.views.search.SearchFragment;import com.meirusfandi.cataloguemovie.views.settings.SettingsFragment;import com.meirusfandi.cataloguemovie.views.upcoming.UpcomingFragment;import butterknife.BindView;import butterknife.ButterKnife;public class MainActivity extends AppCompatActivity {    @BindView(R.id.navigation)    BottomNavigationView navigation;    ActionBar toolbar;    private BottomNavigationView.OnNavigationItemSelectedListener mOnNavigationItemSelectedListener            = new BottomNavigationView.OnNavigationItemSelectedListener() {        @Override        public boolean onNavigationItemSelected(@NonNull MenuItem item) {            Fragment fragment;            switch (item.getItemId()) {                case R.id.nav_home:                    fragment = new HomeFragment();                    openFragment(fragment);                    toolbar.setTitle("Movie Catalogue");                    return true;                case R.id.nav_upcoming:                    fragment = new UpcomingFragment();                    openFragment(fragment);                    toolbar.setTitle("Upcoming Movie");                    return true;                case R.id.nav_search:                    fragment = new SearchFragment();                    openFragment(fragment);                    toolbar.setTitle("Search Movie");                    return true;                case R.id.nav_favorite:                    fragment = new FavoriteFragment();                    openFragment(fragment);                    toolbar.setTitle("Favorite List Movie");                    return true;                case R.id.nav_settings:                    fragment = new SettingsFragment();                    openFragment(fragment);                    toolbar.setTitle("Settings Page");                    return true;            }            return false;        }    };    public void openFragment(Fragment fragment){        FragmentTransaction transaction = getSupportFragmentManager().beginTransaction();        transaction.replace(R.id.base, fragment);        transaction.addToBackStack(null);        transaction.commit();    }    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        toolbar = getSupportActionBar();        toolbar.setTitle("Home");        Fragment fragment = new HomeFragment();        FragmentTransaction transaction = getSupportFragmentManager().beginTransaction();        transaction.replace(R.id.base, fragment);        transaction.addToBackStack(null);        transaction.commit();        ButterKnife.bind(this);        navigation.setOnNavigationItemSelectedListener(mOnNavigationItemSelectedListener);    }}